	$i = 0;
	$len = count($google_obj);

	foreach($google_obj as $key => $g_o) {
		$variant = implode(",",$g_o['variant']);
		foreach($g_o['charsets'] as $subset) {
			$pos = strpos($subsets, $subset);
			if( $pos === false ) {
				$subsets = $subsets . "," . $subset;
